mapper.select:
select * from (select rownum rnum, NO_ID,MEMBER_NAME,BRAND_NAME,NO_CDATE,NO_UDATE,NO_DDATE,NO_FILE,NO_VCOUNT,NO_CON,NO_TITLE from(select NO_ID,MEMBER_NAME,BRAND_NAME,NO_CDATE,NO_UDATE,NO_DDATE,NO_FILE,NO_VCOUNT,NO_CON,NO_TITLE from notice join BRAND using(BRAND_ID) join member using(member_id)
where NO_TITLE like '%' || #{keyword} || '%'
where brand_name like '%' || #{brand} || '%'
where brand_name like '%' || #{brand} || '%' and NO_TITLE like '%' || #{keyword} || '%'
)) where rnum BETWEEN #{startRow} and #{endRow}

mapper.update:
update notice
set NO_VCOUNT = NO_VCOUNT + 1
where NO_ID = #{keyword}
